package com.company;import java.util.LinkedList;import java.util.Queue;import java.util.Scanner;public class P08_OddAndEvenPairs {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        Queue<Integer> numbers = new LinkedList<>();        String[] input = scanner.nextLine().split("\\s+");        for (int i = 0; i < input.length; i++) {            numbers.add(Integer.parseInt(input[i]));        }        int firstNum, secondNum;        int numbersSize = numbers.size();        if (numbersSize % 2 == 1) {            System.out.println("Invalid Length");        }        else {            for (int i = 0; i < numbersSize / 2; i++) {                firstNum = numbers.remove();                secondNum = numbers.remove();                if (firstNum % 2 == 0 && secondNum % 2 == 0) {                    System.out.printf("%d, %d -> both are even \n", firstNum, secondNum);                } else if (firstNum % 2 == 1 && secondNum % 2 == 1) {                    System.out.printf("%d, %d -> both are odd \n", firstNum, secondNum);                } else {                    System.out.printf("%d, %d -> different \n", firstNum, secondNum);                }            }        }    }}